# https://leetcode.com/problems/max-area-of-island/

from typing import List 
import collections

class Solution:
    # BFS
    def maxAreaOfIsland(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        def countIslandBlock(si, sj) -> int:
            d = collections.deque([(si, sj)])
            size = 0
            while d:
                i, j = d.popleft()
                # 同じ状態で最大4つenqueueされるので、ここで再チェックしないと二重カウントする
                if grid[i][j] != 1:
                    continue
                size += 1
                grid[i][j] = -1 # checked
                for x, y in [(i+1, j), (i, j+1), (i-1, j), (i, j-1)]:
                    if 0 <= x < m and 0 <= y < n and grid[x][y] == 1:
                        d.append((x, y))
            return size

        ans = 0
        for i in range(m):
            for j in range(n):
                if grid[i][j] == 1:
                    ans = max(ans, countIslandBlock(i, j))
                else:
                    grid[i][j] = -1 # checked

        return ans

sl = Solution()
